			 		and ed.seclv_code = #{seclv_code}
				
			
			
   				
			 		and UPPER(ed.duty_user_name) LIKE UPPER('%'+#{duty_user_name}+'%') 
				
			
			
   				
			 		and ed.user_name = #{user_name}
				
			
			
   				
