					and ed.print_result='2'
				
			
			
				
					and ed.print_result in ('0','1')
				
			
			
				
					and ep.print_type=2
				
			
			
				
					and ed.paper_state = 0  and ed.job_code is null
				
			
			
				
					and ed.paper_state = 0
				
			
			
				
