			select distinct ed.*,sus.seclv_name,dp.dept_cs
			 from entity_paper ed 
			 left join sec_user_seclv sus on(ed.seclv_code=sus.seclv_code)
			 left join sec_dept dp on(ed.duty_dept_id = dp.dept_id)	
		
		
			
				
					and ed.user_iidd = #{user_iidd}
				
			
			
				
					and ed.dept_id = #{dept_id}
				
			
			
   				
			 		and ed.seclv_code = #{seclv_code}
				
			
			
				
					and ed.dept_id = #{dept_id}
				
			
			
				
					and ed.mergecode_print = #{mergecode_print}
				
			
			
				
					and ed.paper_barcode = #{paper_barcode}
				
			
			
				
					and UPPER(ed.file_title) LIKE UPPER('%'+#{file_title}+'%')
				
			
			
				
					and ed.mergecode_print = #{mergecode_print}
				
			
			
  				
		 			and ed.print_time >= #{start_time}
				
			
			
	  			
			 		and ed.print_time <= #{end_time}
				
			
			
				
					and ed.merge_state = #{merge_state}
				
			
			
				
					and ed.merge_code = #{merge_code}
				
			
		
		and (ed.PID_barcode is null or ed.PID_barcode = '') order by ed.paper_id desc

select count(1) from entity_paper ed left join sec_user_seclv sus on(ed.seclv_code=sus.seclv_code) left join (select project_code,project_name from sys_project) sp on(ed.project_code = sp.project_code) left join (select job_code,jobtype_code,job_status from job_process) jp on(ed.job_code = jp.job_code) left join (select user_iidd, user_name from sec_user) ru on(ru.user_iidd = ed.retrieve_user_iidd) left join (select user_iidd, user_name from sec_user) du on(du.user_iidd = ed.destroy_user_iidd) left join (select box_code, box_name from sys_recyclebox) sr on(sr.box_code = ed.retrieve_box_code) left join (select event_code,keyword_content,print_type from event_print) ep on (ep.event_code = ed.event_code) left join (select dept_id, dept_name, dept_cs from sec_dept) dp on(ed.duty_dept_id = dp.dept_id) where exists ( select text_ from ( select text_,PROC_INST_ID_ from act_hi_varinst where name_ = 'job_code' ) ahc1 where exists ( select act_h.proc_inst_id_ from (select proc_inst_id_ from act_hi_varinst where name_ ='jobType_code' and text_ = 'MERGE_ENTITY') act_h right join ((select proc_inst_id_ from act_hi_taskinst where name_='userApply' and assignee_ = #{user_iidd})) act_t on act_t.proc_inst_id_ = act_h.proc_inst_id_ where ahc1. proc_inst_id_ = act_h.proc_inst_id_ ) and ed.job_code = ahc1.text_ )
and ed.seclv_code = #{seclv_code}
and ed.print_time >= #{start_time}
and ed.print_time <= #{end_time}
and UPPER(ed.paper_barcode) LIKE UPPER('%'+#{paper_barcode}+'%')
and UPPER(ed.file_title) LIKE UPPER('%'+#{file_title}+'%')
and jp.job_status = #{job_status}
and ed.paper_state = #{paper_state}

select ed.*,sus.seclv_name,jp.jobtype_code, jp.job_status from entity_paper ed left join sec_user_seclv sus on(ed.seclv_code=sus.seclv_code) left join (select job_code,jobtype_code,job_status from job_process) jp on(ed.job_code = jp.job_code) left join (select dept_id, dept_name, dept_cs from sec_dept) dp on(ed.duty_dept_id = dp.dept_id) where exists ( select text_ from ( select text_,PROC_INST_ID_ from act_hi_varinst where name_ = 'job_code' ) ahc1 where exists ( select act_h.proc_inst_id_ from (select proc_inst_id_ from act_hi_varinst where name_ ='jobType_code' and text_ = 'MERGE_ENTITY') act_h right join ((select proc_inst_id_ from act_hi_taskinst where name_='userApply' and assignee_ = #{user_iidd})) act_t on act_t.proc_inst_id_ = act_h.proc_inst_id_ where ahc1. proc_inst_id_ = act_h.proc_inst_id_ ) and ed.job_code = ahc1.text_ )
and ed.seclv_code = #{seclv_code}
and ed.print_time >= #{start_time}
and ed.print_time <= #{end_time}
and UPPER(ed.paper_barcode) LIKE UPPER('%'+#{paper_barcode}+'%')
and UPPER(ed.file_title) LIKE UPPER('%'+#{file_title}+'%')
and jp.job_status = #{job_status}
and ed.paper_state = #{paper_state}
order by ed.retrieve_time desc
order by ed.destroy_time desc
order by ed.print_time desc

		
			select count(distinct ed.paper_barcode) 
			 from entity_paper ed 
			 left join sec_user_seclv sus on(ed.seclv_code=sus.seclv_code)
			 left join sec_dept dp on(ed.duty_dept_id = dp.dept_id)	
		
		
			
				
					and ed.user_iidd = #{user_iidd}
				
			
			
				
					and ed.dept_id = #{dept_id}
				
			
			
   				
			 		and ed.seclv_code = #{seclv_code}
				
			
			
				
					and ed.dept_id = #{dept_id}
				
			
			
				
					and ed.mergecode_print = #{mergecode_print}
				
			
			
				
					and ed.paper_barcode = #{paper_barcode}
				
			
			
				
					and UPPER(ed.file_title) LIKE UPPER('%'+#{file_title}+'%')
				
			
			
				
					and ed.mergecode_print = #{mergecode_print}
				
			
			
  				
		 			and ed.print_time >= #{start_time}
				
			
			
	  			
			 		and ed.print_time <= #{end_time}
				
			
			
				
					and ed.merge_state = #{merge_state}
				
			
			
				
					and ed.merge_code = #{merge_code}
				
			
		
		and (ed.PID_barcode is null or ed.PID_barcode = '')
