mapper.select:
select c.contact_id,c.contact_code,c.contact_type,c.group_id,'' user_name,d.dept_name,'' post_name from sec_common_contact c,sec_common_group g ,sec_dept d where c.group_id=g.group_id and c.contact_code=d.dept_id and c.contact_type=2
and d.dept_id in
#{item}
and
d.dept_id like #{item}+'%'
and g.user_iidd=#{user_iidd}

select distinct c.contact_id,c.contact_code,c.contact_type,c.group_id,u.user_name, '' dept_name,p.post_name from sec_common_contact c left join sec_common_group g on (c.group_id=g.group_id and c.contact_type=1) left join sec_user u on(c.contact_code=u.user_iidd) left join sec_user_post p on(u.post_id=p.post_id) left join sec_user_spec us on(u.user_iidd=us.user_iidd) left join sec_dept d on(u.dept_id=d.dept_id) left join sec_role_user ru on(u.user_iidd=ru.user_iidd) left join SEC_ROLE_OPER ae on(ru.role_id=ae.role_id) left join SEC_OPER oe on(ae.oper_code=oe.oper_code) where and g.user_iidd=#{user_iidd}
and oe.oper_code in
#{item}
and ud.dept_id in
#{item}
and
d.dept_id like #{item}+'%'

mapper.update:

	update sec_role 
		set role_name=#{role_name},
			role_desc=#{role_desc}
	where role_id=#{role_id}

mapper.delete:
		
			delete from sec_role_oper
		
		
			
				
				role_id=#{role_id}
				
			
			
        		and oper_code in
        		
					#{item}

mapper.select:
select u.user_iidd FROM sec_user u left join sec_user_post p ON (u.post_id = p.post_id) left join sec_dept dp ON (u.dept_id = dp.dept_id)
UPPER(u.user_iidd) LIKE UPPER('%'+#{user_iidd}+'%')
AND UPPER(u.user_name) LIKE UPPER('%'+#{user_name}+'%')
AND UPPER(u.com_telephone) LIKE UPPER('%'+#{com_telephone}+'%')
AND u.status = #{status}
AND u.post_id = #{post_id}
AND dp.dept_id = #{mainDeptId}
and u.is_sealed='N' and u.user_type !=0
order by u.user_iidd

mapper.update:
update sec_user set user_name=#{user_name}, post_id=#{post_id}, security_code=#{security_code}, idcard=#{idCard}, pass_num=#{pass_num}, print_method=#{print_method}, print_permission=#{print_permission}, rank=#{rank}
,real_user_id=#{real_user_id}
where user_iidd=#{user_iidd}


	update sec_user set
		user_name=#{user_name}
	where user_iidd=#{user_iidd}


	update sec_user set
		user_ppww =#{user_ppww}
	where user_iidd=#{user_iidd}

mapper.select:
		
			SELECT r.*
			FROM sec_role r left join sec_role_user ru on(ru.role_id=r.role_id)
		
		
   			
   				
			 		and subsys_code=#{subsys_code}
				
   			
   			
   				
			 		and role_type=#{role_type}
				
   			
   			
   				
			 		and role_type !=1
				
   			
   			 and is_sealed='N' and ru.user_iidd=#{user_iidd} order by r.role_id


	SELECT count(*) from sec_oper
	WHERE subsys_code = #{subsys_code}  and
		oper_code like #{oper_code}

		
			SELECT * from sec_oper
			where subsys_code = #{subsys_code} and oper_code like #{oper_code}
			ORDER BY oper_code DESC

    	
	        SELECT dept_level_code,dept_level_name,dept_level_desc
	        FROM sec_dept_level
	        WHERE dept_level_code=#{dept_level_code}

	
 		SELECT   dept_type_code,dept_type_name,dept_type_desc,is_sealed
   		FROM   sec_dept_type
   		WHERE  dept_type_code=#{dept_type_code}

mapper.update:

