mapper.select:
		
			SELECT * FROM sec_role r left join sec_role_user ru on(ru.role_id=r.role_id)
			where subsys_code=#{subsys_code} and r.is_sealed='N'
   					and role_type=#{role_type} and ru.user_iidd=#{user_iidd}

mapper.update:

